import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { FormControl, FormGroup } from '@angular/forms';
import { Subject } from 'rxjs';

import { User } from '../models/user';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent {
  public users$: Observable<User[]>;
  public userForm: FormGroup = new FormGroup({
    name: new FormControl(''),
    surname: new FormControl(''),
  });
  public baseUrl: string;
  public http: HttpClient;
  private subject$ = new Subject();

  constructor(http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
    this.http = http;
    this.baseUrl = baseUrl;
    this.users$ = this.replaySubject$.subscribe((fetch: boolean) => {
      if (fetch) {

      }
    })
    http.get<User[]>(baseUrl + 'users').subscribe(result => {
      this.users = result;
    }, error => console.error(error));
  }

  onSubmit(form: FormGroup) {
    console.log('Valid?', form.valid); // true or false
    console.log('Name', form.value.name);
    console.log('Surname', form.value.surname);
    if (form.valid) {
      const { name, surname } = form.value;
      this.http.post<User[]>(this.baseUrl + 'users', form.value).subscribe((result: any) => {
        this.subject$.next();
      }, (error: any) => console.error(error));
    }
  }


}

<h1>Users</h1>

<form [formGroup]="userForm" (ngSubmit)="onSubmit(userForm)">
    <p>Add user</p>
    <input formControlName="name" placeholder="Name" />
    <br />
    <input formControlName="surname" placeholder="Surname" />
    <br />
    <button type="submit">Add</button>
</form>

