import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { FormControl, FormGroup } from '@angular/forms';
import { Subject } from 'rxjs';

import { User } from '../models/user';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent {
  public users: User[] = [];
  public userForm: FormGroup = new FormGroup({
    name: new FormControl(''),
    surname: new FormControl(''),
  });
  public baseUrl: string;
  public http: HttpClient;
  private subject$ = new Subject<User>();

  constructor(http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
    this.http = http;
    this.baseUrl = baseUrl;
    http.get<User[]>(baseUrl + 'users').subscribe((result: any) => {
      this.users = result.users;
    })
    this.subject$.subscribe(user => {
      this.users.push(user);
    })
  }

  onSubmit(form: FormGroup) {
    if (form.valid) {
      this.http.post<User[]>(this.baseUrl + 'users', form.value).subscribe((result: any) => {
        this.subject$.next(result.user);
      }, (error: any) => console.error(error));
    }
  }
}

<style>
    table, td, th {
        border: 1px solid #ddd;
        text-align: left;
    }

    table {
        border-collapse: collapse;
        width: 100%;
        margin: 20px 0;
    }

    th, td {
        padding: 15px;
    }
</style>

<h1>Users</h1>

<form [formGroup]="userForm" (ngSubmit)="onSubmit(userForm)">
    <p>Add user</p>
    <input type="number" formControlName="name" placeholder="Name" />
    <br />
    <input type="number" formControlName="surname" placeholder="Surname" />
    <br />
    <button type="submit">Add</button>
</form>

<ng-container *ngIf="users.length">
    <table>
        <thead>
            <tr>
                <td>ID</td>
                <td>Name</td>
                <td>Surname</td>
                <td>Balance</td>
            </tr>
        </thead>
        <tbody>
            <tr *ngFor="let user of users">
                <td>
                    <a [routerLink]="['/users/' + user.id]">{{user.id}}</a>
                </td>
                <td>{{user.name}}</td>
                <td>{{user.surname}}</td>
            </tr>
        </tbody>
    </table>
</ng-container>
